function Belcotax281Mod({s,d}){const f2=v=>new Intl.NumberFormat('fr-BE',{minimumFractionDigits:2}).format(v);const ae=(s.emps||[]).filter(e=>e.status==='active'||!e.status);const [year,setYear]=useState(new Date().getFullYear()-1);const generateXML=()=>{let xml='<?xml version="1.0" encoding="UTF-8"?>\\n';xml+='<Belcotax xmlns="urn:belcotax:281" version="2.0">\\n';xml+='  <Declarant>\\n';xml+='    <Name>'+(s.co?.name||'ENTREPRISE')+'</Name>\\n';xml+='    <NationalNumber>'+(s.co?.vat||'0000000000').replace(/[^0-9]/g,'')+'</NationalNumber>\\n';xml+='    <Address>'+(s.co?.address||'')+'</Address>\\n';xml+='    <Year>'+year+'</Year>\\n';xml+='  </Declarant>\\n';xml+='  <Fiches>\\n';ae.forEach(emp=>{const brut=+(emp.monthlySalary||emp.gross||emp.brut||0)*12;const onss=brut*0.1307;const pp=Math.max(0,(brut/12-onss/12-945)*0.2867)*12;xml+='    <Fiche281_10>\\n';xml+='      <Beneficiary>\\n';xml+='        <Name>'+(emp.last||'')+'</Name>\\n';xml+='        <FirstName>'+(emp.first||'')+'</FirstName>\\n';xml+='        <NationalNumber>'+(emp.niss||'').replace(/[^0-9]/g,'')+'</NationalNumber>\\n';xml+='        <Address>'+(emp.address||'')+'</Address>\\n';xml+='      </Beneficiary>\\n';xml+='      <Income>\\n';xml+='        <GrossRemuneration>'+brut.toFixed(2)+'</GrossRemuneration>\\n';xml+='        <SocialContributions>'+onss.toFixed(2)+'</SocialContributions>\\n';xml+='        <WithholdingTax>'+pp.toFixed(2)+'</WithholdingTax>\\n';xml+='        <NetRemuneration>'+(brut-onss-pp).toFixed(2)+'</NetRemuneration>\\n';xml+='        <MealVouchers>'+(emp.mealVouchers?(emp.mealVoucherValue||8)*(emp.workDays||220):0).toFixed(2)+'</MealVouchers>\\n';xml+='        <CompanyCar>'+(emp.companyCar?'1':'0')+'</CompanyCar>\\n';xml+='      </Income>\\n';xml+='    </Fiche281_10>\\n'});xml+='  </Fiches>\\n';xml+='  <Summary>\\n';xml+='    <TotalFiches>'+ae.length+'</TotalFiches>\\n';xml+='    <TotalGross>'+ae.reduce((a,e)=>a+(+(e.monthlySalary||e.gross||e.brut||0))*12,0).toFixed(2)+'</TotalGross>\\n';xml+='  </Summary>\\n';xml+='</Belcotax>\\n';const b=new Blob([xml],{type:'application/xml;charset=utf-8'});const u=URL.createObjectURL(b);const a=document.createElement('a');a.href=u;a.download='Belcotax_281_10_'+year+'.xml';a.click()};const generatePDF=()=>{aureuspdf('Fiches 281.10 â€” Recapitulatif '+year,[{type:'title',text:'FICHES 281.10 â€” BELCOTAX'},{type:'text',text:'SPF Finances â€” Exercice '+year,size:8,color:[158,155,147]},{type:'line'},{type:'space',h:4},{type:'kv',k:'Declarant',v:s.co?.name||'[Entreprise]'},{type:'kv',k:'BCE',v:s.co?.vat||'[BCE]'},{type:'kv',k:'Nb fiches',v:String(ae.length)},{type:'space',h:4},{type:'table',cols:['Travailleur','NISS','Brut annuel','ONSS','PP','Net'],rows:ae.map(e=>{const b2=+(e.monthlySalary||e.gross||e.brut||0)*12;const o=b2*0.1307;const p=Math.max(0,(b2/12-o/12-945)*0.2867)*12;return [(e.first||'')+' '+(e.last||''),e.niss||'â€”',f2(b2),f2(o),f2(p),f2(b2-o-p)]})}],{filename:'Belcotax_281_10_recap_'+year,enterprise:s.co?.name||''})};return <div><PH title="Fiches 281.10 â€” Belcotax-on-web" sub={"SPF Finances â€” "+ae.length+" fiches â€” Exercice "+year}/><div style={{display:"flex",gap:10,marginBottom:16,alignItems:"center"}}><select value={year} onChange={e=>setYear(+e.target.value)} style={{padding:"8px 12px",borderRadius:8,border:"1px solid rgba(198,163,78,.15)",background:"rgba(0,0,0,.2)",color:"#e8e6e0",fontSize:12,fontFamily:"inherit"}}>{[2023,2024,2025].map(y=><option key={y} value={y}>{y}</option>)}</select><button onClick={generateXML} style={{padding:"10px 20px",borderRadius:8,border:"none",background:"linear-gradient(135deg,#22c55e,#16a34a)",color:"white",fontWeight:700,fontSize:12,cursor:"pointer",fontFamily:"inherit"}}>Exporter XML Belcotax</button><button onClick={generatePDF} style={{padding:"10px 20px",borderRadius:8,border:"none",background:"linear-gradient(135deg,#c6a34e,#a68a3c)",color:"#0c0b09",fontWeight:700,fontSize:12,cursor:"pointer",fontFamily:"inherit"}}>Recapitulatif PDF</button></div><C><ST>Apercu des fiches {year}</ST><div style={{overflowX:"auto"}}><table style={{width:"100%",borderCollapse:"collapse",fontSize:11}}><thead><tr style={{borderBottom:"1px solid rgba(198,163,78,.2)"}}>{["Travailleur","NISS","Brut annuel","ONSS 13,07%","PP","Net annuel"].map((h,i)=><th key={i} style={{textAlign:"left",padding:"8px 6px",color:"#c6a34e",fontWeight:600,fontSize:10}}>{h}</th>)}</tr></thead><tbody>{ae.map((e,i)=>{const b2=+(e.monthlySalary||e.gross||e.brut||0)*12;const o=b2*0.1307;const p=Math.max(0,(b2/12-o/12-945)*0.2867)*12;return <tr key={i} style={{borderBottom:"1px solid rgba(255,255,255,.03)"}}><td style={{padding:"8px 6px",color:"#e8e6e0"}}>{e.first} {e.last}</td><td style={{padding:"8px 6px",color:"#888",fontSize:10}}>{e.niss||'â€”'}</td><td style={{padding:"8px 6px",color:"#c6a34e",fontWeight:600}}>{f2(b2)}</td><td style={{padding:"8px 6px",color:"#ef4444"}}>{f2(o)}</td><td style={{padding:"8px 6px",color:"#ef4444"}}>{f2(p)}</td><td style={{padding:"8px 6px",color:"#22c55e",fontWeight:600}}>{f2(b2-o-p)}</td></tr>})}</tbody></table></div></C></div>;}

