    return{m,brut:Math.round(brut),onssE:Math.round(onssE),onssW:Math.round(onssW),pp:Math.round(pp),net:Math.round(net),cost:Math.round(cost),cheques:Math.round(cheques),total:Math.round(cost+cheques)};
