// SPRINT 48: RegulPPAnnuelle â€” Regularisation precompte annuelle
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
function RegulPPAnnuelle({s,d}){const ae=(s.emps||[]).filter(e=>e.status==='active'||!e.status);const f2=v=>new Intl.NumberFormat('fr-BE',{minimumFractionDigits:2}).format(v);const [annee]=useState(2026);return <div><PH title="Regularisation PP Annuelle" sub={"Recalcul precompte professionnel fin d'annee â€” "+ae.length+" travailleurs"}/><div style={{display:"grid",gridTemplateColumns:"repeat(4,1fr)",gap:12,marginBottom:18}}>{[{l:"Annee fiscale",v:annee,c:"#c6a34e"},{l:"Travailleurs",v:ae.length,c:"#60a5fa"},{l:"PP retenu total",v:f2(ae.reduce((a,e)=>{const b=e.gross||e.brut||0;return a+(e.pp||Math.round(b*0.22*100)/100)},0)*12),c:"#ef4444"},{l:"PP du annuel",v:f2(ae.reduce((a,e)=>{const b=e.gross||e.brut||0;const annuel=b*12;const ppAnnuel=quickPP?quickPP(annuel/12)*12:Math.round(annuel*0.22*100)/100;return a+ppAnnuel},0)),c:"#fb923c"}].map((k,i)=><div key={i} style={{padding:"14px 16px",background:"rgba(198,163,78,.04)",borderRadius:10,border:"1px solid rgba(198,163,78,.08)"}}><div style={{fontSize:10,color:"#5e5c56",textTransform:"uppercase"}}>{k.l}</div><div style={{fontSize:18,fontWeight:700,color:k.c,marginTop:4}}>{k.v}</div></div>)}</div><C><ST>Regularisation par travailleur</ST><div style={{fontSize:11,color:"#9e9b93",marginBottom:12}}>Comparaison du PP retenu mensuellement vs PP reel du sur base du revenu annuel.<br/>Base legale: AR Precompte professionnel â€” SPF Finances â€” Exercice d'imposition {annee+1}</div>{ae.length===0?<div style={{textAlign:"center",padding:20,color:"#5e5c56"}}>Aucun travailleur</div>:<table style={{width:"100%",borderCollapse:"collapse",fontSize:11}}><thead><tr>{["Travailleur","Brut mensuel","PP mensuel retenu","PP annuel retenu","PP annuel du","Ecart","Action"].map(h=><th key={h} style={{padding:"8px",textAlign:"left",color:"#c6a34e",borderBottom:"2px solid rgba(198,163,78,.2)",fontSize:9}}>{h}</th>)}</tr></thead><tbody>{ae.slice(0,20).map((e,i)=>{const brut=e.gross||e.brut||0;const ppMens=e.pp||Math.round(brut*0.22*100)/100;const ppRetenu=ppMens*12;const annuel=brut*12;const tranches=[{max:15200,taux:0.25},{max:26830,taux:0.40},{max:46440,taux:0.45},{max:Infinity,taux:0.50}];let ppDu=0;let reste=annuel-4880;tranches.forEach(t=>{if(reste>0){const taxable=Math.min(reste,t.max-(annuel-reste>t.max?t.max:annuel-reste-4880));ppDu+=Math.max(0,Math.min(reste,t.max)*t.taux);reste-=t.max}});ppDu=quickPP?quickPP(brut)*12:Math.round(annuel*0.22);const ecart=Math.round((ppRetenu-ppDu)*100)/100;return <tr key={i} style={{borderBottom:"1px solid rgba(255,255,255,.03)"}}><td style={{padding:"6px"}}>{(e.first||e.fn||"")+" "+(e.last||e.ln||"")}</td><td style={{padding:"6px"}}>{f2(brut)}</td><td style={{padding:"6px"}}>{f2(ppMens)}</td><td style={{padding:"6px"}}>{f2(ppRetenu)}</td><td style={{padding:"6px",color:"#fb923c"}}>{f2(ppDu)}</td><td style={{padding:"6px",fontWeight:700,color:ecart>10?"#22c55e":ecart<-10?"#ef4444":"#5e5c56"}}>{ecart>0?"+":""}{f2(ecart)}</td><td style={{padding:"6px",fontSize:9,color:ecart>10?"#22c55e":ecart<-10?"#ef4444":"#5e5c56"}}>{ecart>50?"Trop retenu â€” rembourser":ecart<-50?"Insuffisant â€” retenir":"OK"}</td></tr>})}</tbody></table>}</C><C><ST>Facteurs affectant la regularisation</ST>{[{l:"13e mois / Prime fin d'annee",d:"PP retenu forfaitairement â€” souvent trop eleve",ref:"Art. 88 AR/CIR 92"},{l:"Situation familiale changee",d:"Mariage, naissance, divorce en cours d'annee",ref:"Art. 86 AR/CIR 92"},{l:"Avantages en nature (ATN)",d:"Recalcul ATN voiture, GSM, logement sur l'annee",ref:"Art. 36 CIR/92"},{l:"Bonus CCT 90",d:"Cotisation speciale de 13,07% au lieu de PP normal",ref:"CCT n\u00b090"},{l:"Heures supplementaires",d:"Sursalaire 50%/100% avec regime fiscal favorable",ref:"Art. 154bis CIR/92"},{l:"Reduction PP premier emploi",d:"Reduction structurelle sur les bas salaires",ref:"Art. 289ter CIR/92"}].map((f,i)=><div key={i} style={{display:"flex",justifyContent:"space-between",padding:"8px 0",borderBottom:"1px solid rgba(255,255,255,.03)"}}><div><div style={{fontSize:12,fontWeight:600,color:"#e8e6e0"}}>{f.l}</div><div style={{fontSize:10,color:"#5e5c56"}}>{f.d}</div></div><span style={{fontSize:9,color:"#c6a34e"}}>{f.ref}</span></div>)}</C></div>;}

