function ExportWinbooks({s,d}){const ae=(s.emps||[]).filter(e=>e.status==='active'||!e.status);const f2=v=>new Intl.NumberFormat('fr-BE',{minimumFractionDigits:2}).format(v);const [format,setFormat]=useState("winbooks");const [periode]=useState(new Date().toISOString().slice(0,7));const masseBrut=ae.reduce((a,e)=>a+(e.gross||e.brut||0),0);const exportWinbooks=()=>{const lines=[];const date=new Date().toLocaleDateString('fr-BE').split('/').reverse().join('');lines.push("ACT\t"+date+"\t620000\tD\t"+masseBrut.toFixed(2)+"\tRemunerations brutes");lines.push("ACT\t"+date+"\t621000\tD\t"+(masseBrut*0.2507).toFixed(2)+"\tONSS patronal");ae.forEach(e=>{const brut=e.gross||e.brut||0;const onss=Math.round(brut*0.1307*100)/100;const pp=Math.round(brut*0.22*100)/100;const net=brut-onss-pp;const nom=(e.first||e.fn||"")+" "+(e.last||e.ln||"");lines.push("ACT\t"+date+"\t454000\tC\t"+onss.toFixed(2)+"\tONSS trav. "+nom);lines.push("ACT\t"+date+"\t453100\tC\t"+pp.toFixed(2)+"\tPP "+nom);lines.push("ACT\t"+date+"\t455000\tC\t"+net.toFixed(2)+"\tNet "+nom)});const txt=lines.join("\n");const b=new Blob([txt],{type:"text/plain;charset=utf-8"});const u=URL.createObjectURL(b);const a=document.createElement("a");a.href=u;a.download="winbooks_paie_"+periode+".act";a.click()};const exportBOB50=()=>{const lines=["DBKCODE\tDOCNUMBER\tDOCDATE\tACCOUNTGL\tAMOUNTEUR\tDC\tCOMMENT"];const date=new Date().toISOString().slice(0,10);lines.push("SAL\t1\t"+date+"\t620000\t"+masseBrut.toFixed(2)+"\tD\tRemunerations brutes");lines.push("SAL\t1\t"+date+"\t621000\t"+(masseBrut*0.2507).toFixed(2)+"\tD\tONSS patronal");lines.push("SAL\t1\t"+date+"\t454000\t"+(masseBrut*0.1307).toFixed(2)+"\tC\tONSS travailleur");const ppTotal=ae.reduce((a,e)=>a+Math.round((e.gross||e.brut||0)*0.22*100)/100,0);lines.push("SAL\t1\t"+date+"\t453100\t"+ppTotal.toFixed(2)+"\tC\tPrecompte professionnel");lines.push("SAL\t1\t"+date+"\t455000\t"+(masseBrut-masseBrut*0.1307-ppTotal).toFixed(2)+"\tC\tNet a payer");const txt=lines.join("\n");const b=new Blob([txt],{type:"text/tab-separated-values"});const u=URL.createObjectURL(b);const a=document.createElement("a");a.href=u;a.download="bob50_paie_"+periode+".tsv";a.click()};const exportExact=()=>{const lines=["Journal;Date;Compte;Description;Debit;Credit"];const date=new Date().toLocaleDateString('fr-BE');lines.push("SAL;"+date+";620000;Remunerations;"+masseBrut.toFixed(2)+";");lines.push("SAL;"+date+";621000;ONSS patronal;"+(masseBrut*0.2507).toFixed(2)+";");lines.push("SAL;"+date+";454000;ONSS travailleur;;"+(masseBrut*0.1307).toFixed(2));const ppTotal=ae.reduce((a,e)=>a+Math.round((e.gross||e.brut||0)*0.22*100)/100,0);lines.push("SAL;"+date+";453100;Precompte professionnel;;"+ppTotal.toFixed(2));lines.push("SAL;"+date+";455000;Net a payer;;"+(masseBrut-masseBrut*0.1307-ppTotal).toFixed(2));const txt=lines.join("\n");const b=new Blob([txt],{type:"text/csv;charset=utf-8"});const u=URL.createObjectURL(b);const a=document.createElement("a");a.href=u;a.download="exact_paie_"+periode+".csv";a.click()};return <div><PH title="Export Comptabilite" sub={"Formats natifs Winbooks, BOB50, Exact Online â€” "+ae.length+" travailleurs"}/><div style={{display:"grid",gridTemplateColumns:"repeat(4,1fr)",gap:12,marginBottom:18}}>{[{l:"Masse brute",v:f2(masseBrut),c:"#c6a34e"},{l:"ONSS patronal",v:f2(masseBrut*0.2507),c:"#ef4444"},{l:"ONSS travailleur",v:f2(masseBrut*0.1307),c:"#ef4444"},{l:"Total cout",v:f2(masseBrut*1.2507),c:"#a78bfa"}].map((k,i)=><div key={i} style={{padding:"14px 16px",background:"rgba(198,163,78,.04)",borderRadius:10,border:"1px solid rgba(198,163,78,.08)"}}><div style={{fontSize:10,color:"#5e5c56",textTransform:"uppercase"}}>{k.l}</div><div style={{fontSize:18,fontWeight:700,color:k.c,marginTop:4}}>{k.v}</div></div>)}</div><C><ST>Formats d'export natifs</ST><div style={{display:"grid",gridTemplateColumns:"repeat(3,1fr)",gap:12}}>{[{n:"Winbooks Desktop",fmt:"Format ACT (tab-separated)",ext:".act",fn:exportWinbooks,color:"#22c55e"},{n:"BOB50 / Sage BOB",fmt:"Format TSV avec header DBKCODE",ext:".tsv",fn:exportBOB50,color:"#60a5fa"},{n:"Exact Online",fmt:"CSV avec journal SAL",ext:".csv",fn:exportExact,color:"#a78bfa"}].map((exp,i)=><div key={i} style={{padding:16,background:"rgba(198,163,78,.04)",borderRadius:10,border:"1px solid rgba(198,163,78,.08)",textAlign:"center"}}><div style={{fontSize:14,fontWeight:700,color:exp.color,marginBottom:6}}>{exp.n}</div><div style={{fontSize:10,color:"#5e5c56",marginBottom:10}}>{exp.fmt} ({exp.ext})</div><button onClick={exp.fn} style={{padding:"10px 24px",borderRadius:8,border:"none",background:"rgba("+( i===0?"34,197,94":i===1?"96,165,250":"167,139,250")+",.15)",color:exp.color,fontWeight:600,fontSize:12,cursor:"pointer",fontFamily:"inherit"}}>Exporter {exp.ext}</button></div>)}</div></C><C><ST>Plan comptable utilise</ST>{[{compte:"620000",lib:"Remunerations brutes",type:"D"},{compte:"621000",lib:"ONSS patronal (25,07%)",type:"D"},{compte:"454000",lib:"ONSS travailleur (13,07%)",type:"C"},{compte:"453100",lib:"Precompte professionnel",type:"C"},{compte:"455000",lib:"Net a payer",type:"C"},{compte:"453000",lib:"Cheques-repas patronal",type:"D"},{compte:"617000",lib:"Frais propres employeur",type:"D"}].map((pc,i)=><div key={i} style={{display:"flex",justifyContent:"space-between",padding:"6px 0",borderBottom:"1px solid rgba(255,255,255,.03)"}}><div style={{display:"flex",gap:10}}><span style={{fontSize:12,fontWeight:700,color:"#c6a34e",fontFamily:"monospace"}}>{pc.compte}</span><span style={{fontSize:12,color:"#e8e6e0"}}>{pc.lib}</span></div><span style={{fontSize:10,padding:"2px 8px",borderRadius:10,background:pc.type==="D"?"rgba(239,68,68,.1)":"rgba(34,197,94,.1)",color:pc.type==="D"?"#ef4444":"#22c55e"}}>{pc.type==="D"?"Debit":"Credit"}</span></div>)}</C></div>;}

