          return '<tr><td>'+(e.first||'')+' '+(e.last||'')+'</td><td>'+(e.niss||'N/A')+'</td><td>'+(e.contractType||'CDI')+'</td><td class="r">'+f2(b)+'</td><td class="r">'+f2(b*0.1307)+'</td><td class="r">'+f2(b*0.2507)+'</td></tr>';
